mapper.update:
UPDATE C_BOARD
   SET HIT = HIT + 1
 WHERE CO_NO = #{coNo}

mapper.select:
SELECT CO_NO, NICKNAME, TITLE, CONTENT, CREATE_DATE, MODIFY_DATE, HIT, IP
  FROM C_BOARD
 WHERE CO_NO = #{coNo}

SELECT CO_NO, FILESYSTEM
  FROM C_IMAGE
 WHERE CO_NO = #{coNo}

mapper.update:
UPDATE C_BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
     , MODIFY_DATE = NOW()
 WHERE CO_NO = #{coNo}

mapper.select:
SELECT ATTACH_NO, PATH, ORIGIN, FILESYSTEM, DOWNLOAD_CNT, CO_NO
  FROM C_ATTACH
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE C_ATTACH 
   SET DOWNLOAD_CNT = DOWNLOAD_CNT +1
 WHERE ATTACH_NO = #{attachNo}

mapper.select:
SELECT ATTACH_NO, PATH, ORIGIN, FILESYSTEM, DOWNLOAD_CNT, CO_NO
  FROM C_ATTACH
 WHERE CO_NO = #{coNo}
