mapper.update:
UPDATE FREEBOARD
   SET HIT = HIT + 1
 WHERE FREE_NO = #{freeNo}

mapper.select:
SELECT FREE_NO, NICKNAME, TITLE, CONTENT, CREATE_DATE, MODIFY_DATE, HIT, IP
  FROM FREEBOARD
 WHERE FREE_NO = #{freeNo}

SELECT FREE_NO, FILESYSTEM
  FROM FREEIMAGE
 WHERE FREE_NO = #{freeNo}

mapper.update:
UPDATE FREEBOARD
   SET TITLE = #{title}, CONTENT = #{content}, MODIFY_DATE = NOW()
 WHERE FREE_NO = #{freeNo}
