mapper.select:
SELECT COUNT(*)
  FROM S_GROUP
 WHERE NICKNAME = #{nickname}

SELECT COUNT(*)
  FROM S_ZZIM
 WHERE NICKNAME = #{nickname}

SELECT A.ROW_NUM, A.NICKNAME, A.STUD_NO
  FROM (SELECT ROW_NUMBER() OVER(ORDER BY STUD_NO DESC) AS ROW_NUM, NICKNAME, STUD_NO
        FROM S_ZZIM) A
 WHERE NICKNAME = #{nickname}
   AND STUD_NO = #{studNo}
