mapper.update:
UPDATE Q_BOARD
   SET HIT = HIT + 1
 WHERE QA_NO = #{qaNo}

UPDATE Q_BOARD
   SET ANSWER = #{answer}
 WHERE QA_NO = #{qaNo}

UPDATE Q_CMT
   SET STATE = 0
 WHERE CMT_NO = #{cmtNo}

UPDATE Q_BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
     , MODIFY_DATE = NOW()
 WHERE QA_NO = #{qaNo}
