mapper.update:
UPDATE S_GROUP
   SET HIT = HIT + 1
 WHERE STUD_NO = #{studNo}

mapper.select:
		SELECT STUD_NO, NICKNAME, TITLE, CONTENT, CREATE_DATE, MODIFY_DATE, HIT, GENDER, REGION, WIDO, GDO, LANG, PEOPLE, CONTACT, STUD_DATE, IP
          FROM S_GROUP
		 WHERE STUD_NO = #{studNo}

mapper.update:
UPDATE S_GROUP
   SET TITLE = #{title}
     , CONTENT = #{content}
     , MODIFY_DATE = NOW()
 WHERE STUD_NO = #{studNo}

mapper.select:
SELECT COUNT(*)
  FROM S_ZZIM
 WHERE STUD_NO = #{studNo}
   AND NICKNAME = #{nickname}

SELECT COUNT(*)
  FROM S_ZZIM
 WHERE STUD_NO = #{studNo}

mapper.delete:
DELETE
  FROM S_ZZIM
 WHERE STUD_NO = #{studNo}
   AND NICKNAME = #{nickname}
