mapper.update:
UPDATE S_GROUP
   SET S_HIT = S_HIT + 1
 WHERE S_NO = #{sNo}

mapper.select:
		SELECT S_NO, NICKNAME, S_TITLE, S_CONTENT, S_CREATE_DATE, S_MODIFY_DATE, S_HIT, S_GENDER, S_REGION, S_WIDO, S_GDO, S_LANG, S_PEOPLE, S_CONTACT, S_DATE, S_IP
          FROM S_GROUP
		 WHERE S_NO = #{SNo}

mapper.update:
UPDATE S_GROUP
   SET S_TITLE = #{STitle}
     , S_CONTENT = #{SContent}
     , S_MODIFY_DATE = SYSDATE
 WHERE S_NO = #{SNo}
