mapper.select:
  SELECT USER_NO, ID, NICKNAME, PW, NAME, GRADE, GENDER, EMAIL, MOBILE, BIRTHYEAR, BIRTHDAY, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, EXTRA_ADDRESS, AGREE_CODE, SNS_TYPE, JOIN_DATE, PW_MODIFY_DATE, INFO_MODIFY_DATE, SESSION_ID, SESSION_LIMIT_DATE, BLACK_CNT, USER_STATE
  FROM USERS


	 	ID = #{id}


	 	AND PW = #{pw}


	 	AND EMAIL = #{email}


	 	AND SESSION_ID = #{sessionId}
	 	AND SESSION_LIMIT_DATE > SYSDATE

SELECT ID
  FROM RETIRE_USERS
 WHERE ID = #{id}

mapper.update:
UPDATE ACCESS_LOG
   SET LAST_LOGIN_DATE = SYSDATE
 WHERE ID = #{id}

UPDATE USERS
   SET SESSION_ID = #{sessionId}
     , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE ID = #{id}

UPDATE USERS
   SET PW = #{pw}
     , PW_MODIFY_DATE = SYSDATE
 WHERE USER_NO = #{userNo}

UPDATE USERS
  SET NAME = #{name}
    , NICKNAME = #{nickname}
    , GENDER = #{gender}
    , EMAIL = #{email}
    , MOBILE = #{mobile}
    , BIRTHYEAR = #{birthyear}
    , BIRTHDAY = #{birthday}
    , POSTCODE = #{postcode}
    , ROAD_ADDRESS = #{roadAddress}
    , JIBUN_ADDRESS = #{jibunAddress}
    , DETAIL_ADDRESS = #{detailAddress}
    , EXTRA_ADDRESS = #{extraAddress}
    , INFO_MODIFY_DATE = SYSDATE
WHERE ID = #{id}

mapper.select:
SELECT ID, JOIN_DATE, LAST_LOGIN_DATE, SLEEP_DATE
  FROM SLEEP_USERS
 WHERE ID = #{id}
